//don't change this
int x = 10;

//add 5 to x using +=
x += 5;
Bot.say(x);
Bot.moveRight();

//subtract 10 from x using -=
x -= 10;
Bot.say(x);
Bot.moveRight();

//multiply x by 20 using *=
x *= 20;
Bot.say(x);
Bot.moveRight();

//divide x by 2 using /=
x /= 2;
Bot.say(x);
Bot.moveRight();

//use the moveTo() command
//moveTo() takes string arguments
//use coordinates as an argument
//coordinates are typed like "A3" or "B0"  

Bot.moveTo("A2");
Bot.moveTo("C1");
Bot.moveTo("A3");

//declare two bools
//one should be true
//other should be false
